mapper.select:
SELECT PERFUME_NO , PERFUME_NAME , CATEGORY_NO , BRAND_NO , AMOUNT , PRICE , STORAGE_AMOUNT , MANUFACTURED_DATE , ORDERABLE_STATUS FROM PERFUME WHERE ORDERABLE_STATUS = 'Y'
AND PRICE BETWEEN 0 AND #{ price }
AND PRICE BETWEEN 100000 AND #{ price }
AND PRICE BETWEEN 200000 AND #{ price }
AND PRICE BETWEEN 300000 AND #{ price }

mapper.insert:
INSERT INTO PERFUME
(
   PERFUME_NO
 , PERFUME_NAME
 , CATEGORY_NO
 , BRAND_NO
 , AMOUNT
 , PRICE
 , STORAGE_AMOUNT
 , MANUFACTURED_DATE
 , ORDERABLE_STATUS
)
VALUES
(
   SEQ_PERFUME_NO.NEXTVAL
,  #{ perfumeName }
,  #{ categoryNo }
,  #{ brandNo }
,  #{ amount }
,  #{ price }
,  #{ storageAmount }
,  DEFAULT
,  DEFAULT
)

mapper.update:
UPDATE PERFUME


 PERFUME_NAME = #{ perfumeName }


, CATEGORY_NO = #{ categoryNo }


, BRAND_NO = #{ brandNo }


, AMOUNT = #{ amount }


, PRICE = #{ price }


, STORAGE_AMOUNT = #{ storageAmount }


, ORDERABLE_STATUS = #{ orderableStatus }


WHERE PERFUME_NO = #{ perfumeNo }
